import blogService from '../services/blogs'
import { triggerError, triggerSuccess } from './notificationReducer'

export const addBlog = (title, author, url) => {
  return async dispatch => {
    try {
      const newBlog = await blogService.postBlog(title, author, url)
      dispatch(triggerSuccess('Successfully posted blog'))
      dispatch({
        type: 'ADD_BLOG',
        data: newBlog
      })
    } catch (exception) {
      /*dispatch({
        type: 'SET_ERROR',
        data: {
          msg: 'Error posting blog in action creator'
        }
      })*/
      dispatch(triggerError('Error posting blog in action creator'))
    }
  }
}

export const initBlogs = () => {
  return async dispatch => {
    const blogs = await blogService.getAll()
    blogs.sort((first, second) => {
      if(first.likes > second.likes) {
        return -1
      } else if (first.likes < second.likes) {
        return 1
      } else {
        return 0
      }
    })
    dispatch({
      type: 'INIT_BLOGS',
      data: blogs
    })
  }
}





const blogReducer = (state = [], action) => {
  switch (action.type) {
  case 'ADD_BLOG':
    return [...state, action.data]
  case 'INIT_BLOGS':
    return action.data
  default:
    return state
  }
}

export default blogReducer
